$(document).ready(function(){
	wow = new WOW(
                      {
                      boxClass:     'wow',      // default
                      animateClass: 'animated', // default
                      offset:       0,          // default
                      mobile:       false,       // default
                      live:         true ,       // default
                    }
                    );
                    wow.init();	
	
	
	$('.slidenav').slick({
    	arrows: false,
		autoplay: true,
		dots: false,
		speed: 1500,
		slidesToShow: 4,
		asNavFor: '.slide',
		centerMode: false,
		focusOnSelect: true,
		infinite: true,	
 
  });
	
	$('.slide').slick({
    	arrows: true,
		autoplay: true,
		dots: false,
		speed: 1500,
		utoplaySpeed: 3000,
		slidesToShow: 1,
		asNavFor: '.slidenav',
		infinite: true,
		fade:true,
 
  });

	
});
